class Game{constructor(t){this.word=t,this.limit=this.word.length,this.attempt=1,this.maxAttempt=8,this.wordInput=document.querySelector(".word_input")}nextWord(t){var e=JSON.parse(localStorage.getItem("historic"))||[];e.push({word:this.word,attempts:this.attempt,result:t}),localStorage.setItem("historic",JSON.stringify(e)),setTimeout(()=>{location.reload()},3e3)}validWord(){var o=this.wordInput.length;if(this.word===o)nextWord("victory");else if(this.attempt>=this.maxAttempt)nextWord("defeat");else{let e=0,r=0;for(let t=0;t<this.word.length;t++)this.word[t]===o[t]?e++:o.includes(this.word[t])&&r++;console.log("Letras corretas na posição correta: "+e),console.log("Letras corretas na posição incorreta: "+r)}}start(){var t=document.querySelectorAll(".game_bottom__board--letter"),e=document.querySelector(".enviar_button");this.wordInput.addEventListener("input",()=>{var t=this.limit;this.wordInput.value.length>t&&(this.wordInput.value=this.wordInput.value.slice(0,t))}),t.forEach(t=>{t.getAttribute("term-col")<=this.limit&&t.classList.remove("game_bottom__board--letter")}),e.addEventListener("click",()=>{this.validWord()}),this.wordInput.addEventListener("keypress",t=>{"Enter"===t.key&&this.validWord()})}}